import { useEffect, useRef, useState } from "react";
import axios from "axios";
import {debounce} from "loadsh";

export default function App() {
  const [cardList, setCardList] = useState([]);
  const [limit,setLimit]=useState(9);
  const divRef=useRef(null);

  useEffect(() => {
    const fetchData = () => {
      axios.get(`https://dummyjson.com/recipes?limit=${limit}&skip=0&select=name,image`)
        .then((response) => {
          console.log("response", response.data);
          setCardList(response.data.recipes);
        })
        .catch((error) => {
          console.log("error", error);
        });
    };
    fetchData();
  }, [limit]);

  useEffect(()=>{
    if(divRef.current){
      console.log("here");
      window.addEventListener("scroll",handleScroll);
    return()=>{
      window.removeEventListener("scroll",handleScroll);
    }
    }
  },[divRef.current])

  const handleScroll=()=>{
    console.log("divRef.curret",divRef.current.scrollTop);
   let scrolled=divRef.current.scrollTop;
    setLimit(prev=>prev+3);
  }

  // const handleScollChange=(event)=>{debounce(()=>handleScroll(),100)}

  return (
    <div className="App" style={{height:"800px",overflow:"scroll"}}
    ref={divRef}
    >
      <div style={{ display: "flex", flexDirection: "row", flexWrap: "wrap"}}>
        {cardList.length > 0 &&
          cardList.map((item) => {
            return (
              <div
              key={item.id}
                style={{
                  width: "660px",
                  height: "400px",
                  border: "black 2px solid",
                  margin:"10px 5px"
                }}
              >
                <img
                src={item.image}
                height={300}
                width={380}
                alt="imgae"
                />
                <h3 style={{textAlign:"center"}}>{item.name}</h3>
              </div>
            );
          })}
      </div>
    </div>
  );
}
